import { React, useState } from "react";

const Adjectives = {
  Big: "Big",
  Blue: "Blue",
  Red: "Red",
  Tall: "Tall",
  White: "White",
};

const Noun = {
  Mountain: "Mountain",
  River: "River",
  Rock: "Rock",
  Treeline: "Treeline",
  Deer: "Deer",
  Elk: "Elk",
  Sky: "Sky",
};

const BusinessType = {
  Grill: "Grill",
  Cafe: "Cafe",
  Restaurant: "Restaurant",
  Diner: "Diner",
  Bar: "Bar",
  Grocery: "Grocery",
  Station: "Station",
  Convenience: "Convenience",
};

function Generator() {
  const [name, setName] = useState("");

  const generateName = () => {
    var rand1 = Math.floor(Math.random() * Object.keys(Adjectives).length);
    var rand2 = Math.floor(Math.random() * Object.keys(Noun).length);
    var rand3 = Math.floor(Math.random() * Object.keys(BusinessType).length);

    var randAdjective = Adjectives[Object.keys(Adjectives)[rand1]];
    var randNoun = Noun[Object.keys(Noun)[rand2]];
    var randBusinessType = BusinessType[Object.keys(BusinessType)[rand3]];

    return randAdjective + randNoun + randBusinessType;
  };

  const handleClick = async () => {
    const fetchName = async () => {
      const response = await generateName();
      setName(response);
    };
    fetchName();
  };

  return (
    <div>
      <h1>Your CO Business Name is: {name} </h1>
      <button onClick={handleClick} />
    </div>
  );
}

export default Generator;
