import { React, useState } from "react";
import "./Generator.css";

const Adjectives = {
  Big: "Big",
  Blue: "Blue",
  Red: "Red",
  Tall: "Tall",
  White: "White",
  Creekside: "Creekside",
  Peakview: "Peakview",
  Snowy: "Snowy",
  RockyMountain: "Rocky Mountain",
};

const Noun = {
  Mountain: "Mountain",
  River: "River",
  Rock: "Rock",
  Treeline: "Treeline",
  Deer: "Deer",
  Elk: "Elk",
  Sky: "Sky",
  Bison: "Bison",
};

const BusinessType = {
  Grill: "Grill",
  Cafe: "Cafe",
  Restaurant: "Restaurant",
  Diner: "Diner",
  Bar: "Bar",
  Grocery: "Grocery",
  Station: "Station",
  Convenience: "Convenience",
};

function Generator() {
  const [name, setName] = useState("");

  const generateName = () => {
    const rand1 = Math.floor(Math.random() * Object.keys(Adjectives).length);
    const rand2 = Math.floor(Math.random() * Object.keys(Noun).length);
    const rand3 = Math.floor(Math.random() * Object.keys(BusinessType).length);

    const randAdjective = Adjectives[Object.keys(Adjectives)[rand1]];
    const randNoun = Noun[Object.keys(Noun)[rand2]];
    const randBusinessType = BusinessType[Object.keys(BusinessType)[rand3]];

    const fullName = randAdjective + " " + randNoun + " " + randBusinessType;
    return fullName.toUpperCase();
  };

  const handleClick = async () => {
    const fetchName = async () => {
      const response = await generateName();
      setName(response);
    };
    fetchName();
  };

  return (
    <div className="Generator-body">
      {name === "" ? (
        <button className="Generator-button" onClick={handleClick}>
          Generate Name
        </button>
      ) : (
        <div>
          <div className="Generator-text"> {name} </div>
          <button className="Generator-button" onClick={handleClick}>
            Try Another!
          </button>
        </div>
      )}{" "}
    </div>
  );
}

export default Generator;
